INSERT INTO pagina_web.componente (id_tipo_comp_fk, nombre, estado, descripcion, titulo, id_pagina, id_componente, creacion, modificacion) VALUES ('96afaa70-895e-4289-a55f-5a2054f8726f', 'Libros CIS', 'CREADO', 'Las Publicaciones del Centro de Investigaciones Sociales (CIS) de la Vicepresidencia del Estado Plurinacional de Bolivia son el resultado de una serie de investigaciones realizadas por nuestra institución durante los años 2013 y 2019.  Estas obras tienen por objetivo contribuir, mediante análisis detallado de la realidad boliviana, al fortalecimiento de la investigación de y sobre el Estado Plurinacional, priorizando el estudio acerca de los sujetos sociales que lo componen así como la caracterización de su institucionalidad, desarrollo económico, político y social, además de las referencias históricas y teóricas de la raíz del mismo. ', 'LIBROS CIS', '3cb5f4ca-d3a7-4b47-b237-cc6fa8ae17f7', '09caf691-3bd7-45c7-a1ad-aeb32698a8f5', '2021-12-22 10:17:04.036508', '2021-12-22 10:17:04.036508');
