INSERT INTO pagina_web.seccion_propia (titulo, descripcion, url_imagen, estado, link_discurso, id_componente, id_sec_pro, creacion, modificacion, id_imagen) VALUES ('David Choquehuanca Céspedes', 'EL nuevo tiempo significa escuchar el mensaje de nuestros pueblos que vienen del fondo de sus corazones, significa sanar heridas, mirarnos con respecto, recuperar la patria, soñar juntos, construir hermandad, armonía, integración, esperanza, para garantizar la paz y la felicidad de las nuevas generaciones, solo así podremos alcanzar el vivir bien y gobernarnos nosotros mismos.', '6058f609-23ec-47a0-bfb5-76aa53d8a751', 'CREADO', NULL, 'bcfc9e3a-32c4-4722-b5a4-53e516c3c3e0', NULL, '2021-12-22 10:18:22.736593', '2021-12-22 10:18:22.736593', '51dffdda-89f2-4a3e-5fbf-b6111487e671');
INSERT INTO pagina_web.seccion_propia (titulo, descripcion, url_imagen, estado, link_discurso, id_componente, id_sec_pro, creacion, modificacion, id_imagen) VALUES ('TENTA (DIÁLOGOS VIRTUALES)', 'El Centro de Investigaciones Sociales de la Vicepresidencia (CIS) tiene el objetivo de generar reflexión crítica e investigación de alta calidad académica para aportar a la comprensión y fortalecimiento del Estado Plurinacional de Bolivia. Por ese motivo nos interesa promover nuevo conocimiento mediante el financiamiento de Proyectos de Investigación en el área social y visibilizar los trabajos más sobresalientes mediante el Concurso Nacional de Tesis.', NULL, 'CREADO', NULL, '1ded09a6-0294-402e-b538-fd38d315e561', 'cbfc9e3a-32c4-4122-b5a4-53e516c3c30w', '2022-01-09 21:07:04.545326', '2022-01-09 21:07:04.545326', '19317083-12b7-4c00-872b-1a79b6af16ff');



